Scripts.OnEnter:
local activeEntryInfo = C_LFGList.GetActiveEntryInfo()
if(activeEntryInfo) then local numApplicants, numActiveApplicants = C_LFGList.GetNumApplicants() self.BackgroundHover:Show()
GameTooltip:SetOwner(self, "ANCHOR_RIGHT") GameTooltip:SetText(string.format(LFG_LIST_PENDING_APPLICANTS, numActiveApplicants))
if(activeEntryInfo.questID and activeEntryInfo.questID > 0) then GameTooltip:AddLine(LFG_TYPE_QUEST .. ": " .. C_QuestLog.GetTitleForQuestID(activeEntryInfo.questID))
end
GameTooltip:Show() end
